import { AttackDistribution } from "@shared/game/network/model/attack/attack-distribution";
import { ClientEvent as ClientSideEvent } from "@shared/game/network/model/event/client-event";

// ServerSideEvents are generated by server player.
export interface ServerSideEvent {
  // Attack distribution should only be handled by local player,
  //  remote players will handle the attack distributions on acknowledgement instead.
  attackDistributions?: AttackDistribution[];

  // Indicates that the server has dropped this player.
  disconnect?: boolean;
}

export interface ServerPlayerEvent {
  playerIndex: number,
  clientEvent?: ClientSideEvent,
  serverEvent?: ServerSideEvent,
}

export interface ServerEvent {
  roomId: number;
  playerEvents: ServerPlayerEvent[];
}
